import axios from 'axios';
import { TableHead, TableRow, TableCell, TableBody, TableContainer, Paper, TextField, MenuItem } from '@mui/material';
import { styled } from '@mui/system';

interface Lead {
  id: number;
  title: string;
  source: string;
  budget: number;
